
public class IntFloatDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int varZahl1 = 10 / 3;
		float varZahl2 = 10f / 3f; // das f hinter den Zahlen sagt dem Compiler, dass es sich um eine Floatzahl handelt
		double varZahl3 = 10d / 3d; // das d hinter den Zahlen sagt dem Compiler, dass es sich um eine Doublezahl handelt
		
		System.out.println("int " + varZahl1);
		System.out.println("float " + varZahl2);
		System.out.println("double " + varZahl3);
		
	}

}
